% self_similarity_matrices% Octave 4% edit <---%==========================================================================clear% check if statistics package is loaded:pkg_list = pkg('list');pkg_name = 'statistics';statistics_package = 0;for i = 1:length(pkg_list)  if strcmp(pkg_list{i}.name,pkg_name)    statistics_package = 1;  endend% make signal:L = 10; % <---------- lengthsig = rand(L,3);%..........................................................................% Euclidean distance without sqrt:start_col = 2;for row = 1:L  for col = start_col:L    Euclidean2_xyz(row,col) = sum((sig(row,:)-sig(col,:)).^2); % xyz    Euclidean2_xyz(col,row) = Euclidean2_xyz(row,col);        Euclidean2_x(row,col) = sum((sig(row,1)-sig(col,1)).^2); % x     Euclidean2_x(col,row) = Euclidean2_x(row,col);        Euclidean2_y(row,col) = sum((sig(row,2)-sig(col,2)).^2); % y     Euclidean2_y(col,row) = Euclidean2_y(row,col);        Euclidean2_z(row,col) = sum((sig(row,3)-sig(col,3)).^2); % z     Euclidean2_z(col,row) = Euclidean2_z(row,col);      end  start_col = start_col+1;endfiguresubplotcount = 1;subplotsn = 3;subplot(1,subplotsn,subplotcount)subplotcount = subplotcount + 1;imagesc(Euclidean2_xyz)title('Euclidean2(xyz)')subplot(1,subplotsn,subplotcount)subplotcount = subplotcount + 1;imagesc(Euclidean2_x + Euclidean2_y + Euclidean2_z)title('Euclidean2(x) + Euclidean2(y) + Euclidean2(z)')if statistics_package  subplot(1,subplotsn,subplotcount)  subplotcount = subplotcount + 1;  imagesc(pdist2(sig,sig,'sqeuclidean'))  title('pdist2(sig,sig,''sqeuclidean'')')end%..........................................................................% Euclidean distance:Euclidean_xyz = Euclidean2_xyz .^ 0.5;Euclidean_x = Euclidean2_x .^ 0.5;Euclidean_y = Euclidean2_y .^ 0.5;Euclidean_z = Euclidean2_z .^ 0.5;figuresubplotcount = 1;subplotsn = 3;subplot(1,subplotsn,subplotcount)subplotcount = subplotcount + 1;imagesc(Euclidean_xyz)title('Euclidean(xyz)')subplot(1,subplotsn,subplotcount)subplotcount = subplotcount + 1;imagesc( Euclidean_x + Euclidean_y + Euclidean_z )title('Euclidean(x) + Euclidean(y) + Euclidean(z)')if statistics_package  subplot(1,subplotsn,subplotcount)  subplotcount = subplotcount + 1;  imagesc(pdist2(sig,sig))  title('pdist2(sig,sig)')end%..........................................................................% Cityblock distance:start_col = 2;for row = 1:L  for col = start_col:L    Cityblock_xyz(row,col) = sum(abs(sig(row,:)-sig(col,:))); % xyz    Cityblock_xyz(col,row) = Cityblock_xyz(row,col);    Cityblock_x(row,col) = sum(abs(sig(row,1)-sig(col,1))); % x     Cityblock_x(col,row) = Cityblock_x(row,col);        Cityblock_y(row,col) = sum(abs(sig(row,2)-sig(col,2))); % y     Cityblock_y(col,row) = Cityblock_y(row,col);        Cityblock_z(row,col) = sum(abs(sig(row,3)-sig(col,3))); % z     Cityblock_z(col,row) = Cityblock_z(row,col);          end  start_col = start_col+1;endfiguresubplotcount = 1;subplotsn = 3;subplot(1,subplotsn,subplotcount)subplotcount = subplotcount + 1;imagesc(Cityblock_xyz)title('Cityblock(xyz)')subplot(1,subplotsn,subplotcount)subplotcount = subplotcount + 1;imagesc(Cityblock_x + Cityblock_y + Cityblock_z)title('Cityblock(x) + Cityblock(y) + Cityblock(z)')if statistics_package  subplot(1,subplotsn,subplotcount)  subplotcount = subplotcount + 1;  imagesc(pdist2(sig,sig,'L1'))  title('pdist2(sig,sig,''L1'')')end%..........................................................................% Cosine distance:sig_cos_xyz = sig ./ repmat ( sqrt(sum (sig.^2,2) ), [1 3]);Cosine_xyz = 1 - sig_cos_xyz*sig_cos_xyz';sig_cos_x = sig(:,1) ./ sqrt( sum ( sig(:,1).^2,2 ) );Cosine_x = 1 - sig_cos_x*sig_cos_x';sig_cos_y = sig(:,2) ./ sqrt( sum ( sig(:,2).^2,2 ) );Cosine_y = 1 - sig_cos_y*sig_cos_y';sig_cos_z = sig(:,3) ./ sqrt( sum ( sig(:,3).^2,2 ) );Cosine_z = 1 - sig_cos_z*sig_cos_z';figuresubplotcount = 1;subplotsn = 3;subplot(1,subplotsn,subplotcount)subplotcount = subplotcount + 1;imagesc(Cosine_xyz)title('Cosine(xyz)')subplot(1,subplotsn,subplotcount)subplotcount = subplotcount + 1;imagesc(Cosine_x + Cosine_y + Cosine_z)title('Cosine(x) + Cosine(y) + Cosine(z)')if statistics_package  subplot(1,subplotsn,subplotcount)  subplotcount = subplotcount + 1;  imagesc(pdist2(sig,sig,'cosine'))  title('pdist2(sig,sig,''cosine'')')end